@prefix fsec: <https://growgraph.dev/fsec#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix schema: <https://schema.org/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

fsec:AnnualReport a rdfs:Class ;
    rdfs:label "Annual Report" ;
    rdfs:comment "A specific type of filing that provides financial information for a year." ;
    rdfs:subClassOf fsec:SecuritiesFiling .

fsec:Company a rdfs:Class ;
    rdfs:label "Company" ;
    rdfs:comment "An organization that issues securities." .

fsec:CompanyType a rdfs:Class ;
    rdfs:label "Company Type" ;
    rdfs:comment "Categorizes companies based on their filing status, such as large accelerated filer or smaller reporting company." .

fsec:DebtSecurity a rdfs:Class ;
    rdfs:label "Debt Security" ;
    rdfs:comment "A type of security representing borrowed money to be repaid." ;
    rdfs:subClassOf fsec:Security .

fsec:Document a rdfs:Class ;
    rdfs:label "Document" ;
    rdfs:comment "A general document, such as a financial filing or report." .

fsec:EarningsPerShare a rdfs:Class ;
    rdfs:label "Earnings Per Share" ;
    rdfs:comment "A financial metric indicating the portion of a company's profit allocated to each outstanding share of common stock." ;
    rdfs:subClassOf fsec:FinancialPerformance .

fsec:Exchange a rdfs:Class ;
    rdfs:label "Exchange" ;
    rdfs:comment "A marketplace where securities are bought and sold." .

fsec:FinancialMetrics a rdfs:Class ;
    rdfs:label "Financial Metrics" ;
    rdfs:comment "Abstract class representing various financial performance indicators." .

fsec:FinancialPerformance a rdfs:Class ;
    rdfs:label "Financial Performance" ;
    rdfs:comment "Metrics representing the financial performance of a company, such as revenue and net income." .

fsec:FinancialStatement a rdfs:Class ;
    rdfs:label "Financial Statement" ;
    rdfs:comment "A document providing detailed financial information about a company." ;
    rdfs:subClassOf fsec:Document .

fsec:GrossMargin a rdfs:Class ;
    rdfs:label "Gross Margin" ;
    rdfs:comment "A financial metric representing the difference between revenue and cost of goods sold divided by revenue." ;
    rdfs:subClassOf fsec:FinancialPerformance .

fsec:NetIncome a rdfs:Class ;
    rdfs:label "Net Income" ;
    rdfs:comment "A financial metric representing the total profit of a company after all expenses have been deducted from revenue." ;
    rdfs:subClassOf fsec:FinancialPerformance .

fsec:OperatingIncome a rdfs:Class ;
    rdfs:label "Operating Income" ;
    rdfs:comment "A financial metric representing the income a company generates from its normal business operations." ;
    rdfs:subClassOf fsec:FinancialPerformance .

fsec:QuarterlyReport a rdfs:Class ;
    rdfs:label "Quarterly Report" ;
    rdfs:comment "A specific type of filing that provides financial information for a quarter." ;
    rdfs:subClassOf fsec:SecuritiesFiling .

fsec:Report a rdfs:Class ;
    rdfs:label "Report" ;
    rdfs:comment "A specific type of document used for periodic reporting purposes." ;
    rdfs:subClassOf fsec:Document .

fsec:RiskFactor a rdfs:Class ;
    rdfs:label "Risk Factor" ;
    rdfs:comment "Factors that may affect the financial performance or risk profile of a company or security." .

fsec:SecuritiesFiling a rdfs:Class ;
    rdfs:label "Securities Filing" ;
    rdfs:comment "A filing made with a regulatory authority related to securities, such as a Form 10-Q." ;
    rdfs:subClassOf fsec:Document .

fsec:Security a rdfs:Class ;
    rdfs:label "Security" ;
    rdfs:comment "A financial instrument representing ownership, debt, or other rights." .

fsec:TotalExpenses a rdfs:Class ;
    rdfs:label "Total Expenses" ;
    rdfs:comment "A financial metric representing the total costs incurred by a company in its operations." ;
    rdfs:subClassOf fsec:FinancialPerformance .

fsec:TotalRevenue a rdfs:Class ;
    rdfs:label "Total Revenue" ;
    rdfs:comment "A financial metric representing the total income generated by a company from its business activities." ;
    rdfs:subClassOf fsec:FinancialPerformance .

fsec:TransitionReport a rdfs:Class ;
    rdfs:label "Transition Report" ;
    rdfs:comment "A specific type of filing that provides financial information during a transition period." ;
    rdfs:subClassOf fsec:SecuritiesFiling .

fsec:hasCompanyType a rdf:Property ;
    rdfs:label "has company type" ;
    rdfs:comment "Associates a company with its type, such as large accelerated filer or smaller reporting company." ;
    rdfs:domain fsec:Company ;
    rdfs:range fsec:CompanyType .

fsec:hasContactNumber a rdf:Property ;
    rdfs:label "has contact number" ;
    rdfs:comment "Relates an organization to its official contact number." ;
    rdfs:domain fsec:Company,
        schema:Organization ;
    rdfs:range xsd:string .

fsec:hasDate a rdf:Property ;
    rdfs:label "has date" ;
    rdfs:comment "Associates a document with a specific date." ;
    rdfs:domain fsec:Document ;
    rdfs:range xsd:date .

fsec:hasEarningsPerShare a rdf:Property ;
    rdfs:label "has earnings per share" ;
    rdfs:comment "Links a financial statement to its earnings per share value." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:decimal .

fsec:hasEffectiveDate a rdf:Property ;
    rdfs:label "has effective date" ;
    rdfs:comment "Associates a filing with its effective date." ;
    rdfs:domain fsec:SecuritiesFiling ;
    rdfs:range xsd:date .

fsec:hasExchange a rdf:Property ;
    rdfs:label "has exchange" ;
    rdfs:comment "Specifies the exchange where a security is listed." ;
    rdfs:domain fsec:Security ;
    rdfs:range fsec:Exchange,
        schema:Exchange .

fsec:hasExchangeListing a rdf:Property ;
    rdfs:label "has exchange listing" ;
    rdfs:comment "Links a security to its exchange listing details." ;
    rdfs:domain fsec:Security ;
    rdfs:range fsec:Exchange .

fsec:hasFilingDate a rdf:Property ;
    rdfs:label "has filing date" ;
    rdfs:comment "Associates a filing with its submission date." ;
    rdfs:domain fsec:SecuritiesFiling ;
    rdfs:range xsd:date .

fsec:hasFilingType a rdf:Property ;
    rdfs:label "has filing type" ;
    rdfs:comment "Specifies the type of filing associated with a document, such as quarterly or annual report." ;
    rdfs:domain fsec:SecuritiesFiling ;
    rdfs:range xsd:string .

fsec:hasFinancialMetric a rdf:Property ;
    rdfs:label "has financial metric" ;
    rdfs:comment "Links a financial performance to specific financial metrics like earnings per share, gross margin, etc." ;
    rdfs:domain fsec:FinancialPerformance ;
    rdfs:range fsec:FinancialMetrics .

fsec:hasFinancialPeriod a rdf:Property ;
    rdfs:label "has financial period" ;
    rdfs:comment "Associates a financial statement with a specific reporting period." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:string .

fsec:hasFinancialPeriodEnd a rdf:Property ;
    rdfs:label "has financial period end" ;
    rdfs:comment "Associates a financial statement with the end date of its reporting period." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:date .

fsec:hasFinancialPeriodStart a rdf:Property ;
    rdfs:label "has financial period start" ;
    rdfs:comment "Associates a financial statement with the start date of its reporting period." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:date .

fsec:hasFinancialStatement a rdf:Property ;
    rdfs:label "has financial statement" ;
    rdfs:comment "Links a report to its associated financial statements." ;
    rdfs:domain fsec:Report ;
    rdfs:range fsec:FinancialStatement .

fsec:hasGrossMargin a rdf:Property ;
    rdfs:label "has gross margin" ;
    rdfs:comment "Links a financial statement to its gross margin value." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:decimal .

fsec:hasIssuer a rdf:Property ;
    rdfs:label "has issuer" ;
    rdfs:comment "Relates a security to the organization issuing it." ;
    rdfs:domain fsec:Security ;
    rdfs:range fsec:Company,
        schema:Organization .

fsec:hasNetIncome a rdf:Property ;
    rdfs:label "has net income" ;
    rdfs:comment "Links a financial performance metric to its net income value." ;
    rdfs:domain fsec:FinancialPerformance ;
    rdfs:range xsd:decimal .

fsec:hasOperatingIncome a rdf:Property ;
    rdfs:label "has operating income" ;
    rdfs:comment "Links a financial statement to its operating income value." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:decimal .

fsec:hasPrincipalOffice a rdf:Property ;
    rdfs:label "has principal office" ;
    rdfs:comment "Relates an organization to its principal place of business." ;
    rdfs:domain fsec:Company,
        schema:Organization ;
    rdfs:range schema:Place .

fsec:hasReportingPeriod a rdf:Property ;
    rdfs:label "has reporting period" ;
    rdfs:comment "Associates a financial statement with its reporting period." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:string .

fsec:hasRevenue a rdf:Property ;
    rdfs:label "has revenue" ;
    rdfs:comment "Links a financial performance metric to its revenue value." ;
    rdfs:domain fsec:FinancialPerformance ;
    rdfs:range xsd:decimal .

fsec:hasRiskFactor a rdf:Property ;
    rdfs:label "has risk factor" ;
    rdfs:comment "Links a financial document to its associated risk factors." ;
    rdfs:domain fsec:Document,
        fsec:FinancialStatement ;
    rdfs:range xsd:string,
        fsec:RiskFactor .

fsec:hasRiskFactorForDocument a rdf:Property ;
    rdfs:label "has risk factor for document" ;
    rdfs:comment "Links a risk factor to a specific financial document or statement." ;
    rdfs:domain fsec:RiskFactor ;
    rdfs:range fsec:Document .

fsec:hasRiskFactorForFiling a rdf:Property ;
    rdfs:label "has risk factor for filing" ;
    rdfs:comment "Links a filing to its associated risk factors." ;
    rdfs:domain fsec:SecuritiesFiling ;
    rdfs:range fsec:RiskFactor .

fsec:hasRiskFactorForSecurity a rdf:Property ;
    rdfs:label "has risk factor for security" ;
    rdfs:comment "Links a risk factor to a specific security." ;
    rdfs:domain fsec:RiskFactor ;
    rdfs:range fsec:Security .

fsec:hasShareholder a rdf:Property ;
    rdfs:label "has shareholder" ;
    rdfs:comment "Links a company to its shareholders." ;
    rdfs:domain fsec:Company ;
    rdfs:range schema:Person .

fsec:hasShareholderEquity a rdf:Property ;
    rdfs:label "has shareholder equity" ;
    rdfs:comment "Links a financial statement to its shareholder equity value." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:decimal .

fsec:hasTotalAssets a rdf:Property ;
    rdfs:label "has total assets" ;
    rdfs:comment "Links a financial statement to its total assets value." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:decimal .

fsec:hasTotalLiabilities a rdf:Property ;
    rdfs:label "has total liabilities" ;
    rdfs:comment "Links a financial statement to its total liabilities value." ;
    rdfs:domain fsec:FinancialStatement ;
    rdfs:range xsd:decimal .

fsec:hasTradingSymbol a rdf:Property ;
    rdfs:label "has trading symbol" ;
    rdfs:comment "Relates a security to its trading symbol." ;
    rdfs:domain fsec:Security ;
    rdfs:range xsd:string .

fsec:isCompliantWithRegulation a rdf:Property ;
    rdfs:label "is compliant with regulation" ;
    rdfs:comment "Indicates whether a company is compliant with relevant regulations." ;
    rdfs:domain fsec:Company ;
    rdfs:range xsd:boolean .

fsec:issues a rdf:Property ;
    rdfs:label "issues" ;
    rdfs:comment "Links a company to the securities it issues." ;
    rdfs:domain fsec:Company ;
    rdfs:range fsec:Security .
