current_ontology.graph:
@prefix co: <https://example.com/finont#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

co:Company a rdfs:Class ;
    rdfs:label "Company" ;
    rdfs:comment "An organization engaged in commercial, industrial, or professional activities." .

co:EarningsReport a rdfs:Class ;
    rdfs:label "Earnings Report" ;
    rdfs:comment "A specific type of financial report that details a company's earnings over a specific period." ;
    rdfs:subClassOf co:FinancialReport .

co:FinancialReport a rdfs:Class ;
    rdfs:label "Financial Report" ;
    rdfs:comment "A report that provides a summary of a company's financial performance over a specific period." .

co:FinancialStatement a rdfs:Class ;
    rdfs:label "Financial Statement" ;
    rdfs:comment "A formal record of the financial activities and position of a business, person, or other entity." .

co:LegalProceedings a rdfs:Class ;
    rdfs:label "Legal Proceedings" ;
    rdfs:comment "Any legal actions or proceedings that may impact the company." .

co:MarketRisk a rdfs:Class ;
    rdfs:label "Market Risk" ;
    rdfs:comment "Disclosures regarding risks that may affect the company's financial performance due to market fluctuations." .

co:OperatingExpenses a rdfs:Class ;
    rdfs:label "Operating Expenses" ;
    rdfs:comment "Expenses incurred in the normal course of business operations." .

co:RiskFactors a rdfs:Class ;
    rdfs:label "Risk Factors" ;
    rdfs:comment "Factors that could potentially affect the company's performance and financial results." .

co:Stock a rdfs:Class ;
    rdfs:label "Stock" ;
    rdfs:comment "A type of security that signifies ownership in a company and represents a claim on part of the companyâs assets and earnings." .

co:hasCompany a rdf:Property ;
    rdfs:label "has Company" ;
    rdfs:comment "Relates a financial report to the company it pertains to." ;
    rdfs:domain co:FinancialReport ;
    rdfs:range co:Company .

co:hasEarningsPerShare a rdf:Property ;
    rdfs:label "has Earnings Per Share" ;
    rdfs:comment "Represents the earnings per share reported in a financial statement." ;
    rdfs:domain co:FinancialStatement ;
    rdfs:range xsd:decimal .

co:hasLegalProceedings a rdf:Property ;
    rdfs:label "has Legal Proceedings" ;
    rdfs:comment "Links a financial report to any legal proceedings that may affect the company." ;
    rdfs:domain co:FinancialReport ;
    rdfs:range co:LegalProceedings .

co:hasMarketRisk a rdf:Property ;
    rdfs:label "has Market Risk" ;
    rdfs:comment "Links a financial report to its associated market risk disclosures." ;
    rdfs:domain co:FinancialReport ;
    rdfs:range co:MarketRisk .

co:hasNetIncome a rdf:Property ;
    rdfs:label "has Net Income" ;
    rdfs:comment "Represents the net income reported in a financial statement." ;
    rdfs:domain co:FinancialStatement ;
    rdfs:range xsd:decimal .

co:hasOperatingExpenses a rdf:Property ;
    rdfs:label "has Operating Expenses" ;
    rdfs:comment "Represents the total operating expenses reported in the financial statements." ;
    rdfs:domain co:FinancialStatement ;
    rdfs:range xsd:decimal .

co:hasRiskFactors a rdf:Property ;
    rdfs:label "has Risk Factors" ;
    rdfs:comment "Connects financial reports to the risk factors identified that could impact performance." ;
    rdfs:domain co:FinancialReport ;
    rdfs:range co:RiskFactors .

co:hasStock a rdf:Property ;
    rdfs:label "has Stock" ;
    rdfs:comment "Relates a company to its issued stocks." ;
    rdfs:domain co:Company ;
    rdfs:range co:Stock .

co:hasTotalAssets a rdf:Property ;
    rdfs:label "has Total Assets" ;
    rdfs:comment "Represents the total assets reported in a financial statement." ;
    rdfs:domain co:FinancialStatement ;
    rdfs:range xsd:decimal .

co:hasTotalLiabilities a rdf:Property ;
    rdfs:label "has Total Liabilities" ;
    rdfs:comment "Represents the total liabilities reported in a financial statement." ;
    rdfs:domain co:FinancialStatement ;
    rdfs:range xsd:decimal .

co:includesFinancialStatement a rdf:Property ;
    rdfs:label "includes Financial Statement" ;
    rdfs:comment "Indicates that a financial report includes one or more financial statements." ;
    rdfs:domain co:FinancialReport ;
    rdfs:range co:FinancialStatement .

